export interface IPosition {
  x: number;
  y: number;
  rotate?: number;
  scaleX?: number;
  scaleY?: number;
}

export class Position implements IPosition {

  constructor(
    public x: number,
    public y: number,
    public rotate = 0,
    public scaleX = 1,
    public scaleY = 1
  ) {}

  static fromEvent(e: MouseEvent | TouchEvent, el: any = null) {
    /**
     * Fix issue: Resize doesn't work on Windows10 IE11 (and on some windows 7 IE11)
     * https://github.com/xieziyu/ngx-draggable-resize/issues/164
     * e instanceof MouseEvent check returns false on IE11
     */
    if (this.isMouseEvent(e)) {
      return new Position(e.clientX, e.clientY);
    } else {
      if (el === null || e.changedTouches.length === 1) {
        return new Position(
          e.changedTouches[0].clientX,
          e.changedTouches[0].clientY
        );
      }

      /**
       * Fix issue: Multiple phone draggables at the same time
       * https://github.com/xieziyu/ngx-draggable-resize/issues/128
       */
      for (let i = 0; i < e.changedTouches.length; i++) {
        if (e.changedTouches[i].target === el) {
          return new Position(
            e.changedTouches[i].clientX,
            e.changedTouches[i].clientY
          );
        }
      }
    }
  }

  static isMouseEvent(e: MouseEvent | TouchEvent): e is MouseEvent {
    return Object.prototype.toString.apply(e).indexOf("MouseEvent") === 8;
  }

  static isIPosition(obj): obj is IPosition {
    return !!obj && "x" in obj && "y" in obj;
  }

  static getCurrent(el: Element) {
    let pos = new Position(0, 0,0,0,0);

    if (window) {
      const computed = window.getComputedStyle(el);
      if (computed) {
        let x = parseInt(computed.getPropertyValue("left"), 10);
        let y = parseInt(computed.getPropertyValue("top"), 10);
        pos.x = isNaN(x) ? 0 : x;
        pos.y = isNaN(y) ? 0 : y;
      }
      return pos;
    } else {
      console.error("Not Supported!");
      return null;
    }
  }

  static copy(p: IPosition) {
    return new Position(0, 0, 0, 1, 1).set(p);
  }

  get value(): IPosition {
    return {
      x: this.x,
      y: this.y,
      rotate: this.rotate,
      scaleX: this.scaleX,
      scaleY: this.scaleY
    };
  }

  add(p: IPosition) {
    this.x += p.x;
    this.y += p.y;
    return this;
  }

  subtract(p: IPosition) {
    this.x -= p.x;
    this.y -= p.y;
    return this;
  }

  multiply(n: number) {
    this.x *= n;
    this.y *= n;
  }

  divide(n: number) {
    this.x /= n;
    this.y /= n;
  }

  reset() {
    this.x = 0;
    this.y = 0;
    this.rotate = 0;
    this.scaleX = 1;
    this.scaleY = 1;
    return this;
  }

  set(p: IPosition) {
    this.x = p.x;
    this.y = p.y;
    this.rotate = p.rotate;
    this.scaleY = p.scaleY;
    this.scaleX = p.scaleX;
    return this;
  }
}
import { Renderer2 } from '@angular/core';

export class HelperBlock {
  protected _helper: Element;
  private _added = false;

  constructor(
    protected parent: Element,
    protected renderer: Renderer2
  ) {
    // generate helper div
    let helper = renderer.createElement('div');
    renderer.setStyle(helper, 'position', 'absolute');
    renderer.setStyle(helper, 'width', '100%');
    renderer.setStyle(helper, 'height', '100%');
    renderer.setStyle(helper, 'background-color', 'transparent');
    renderer.setStyle(helper, 'top', '0');
    renderer.setStyle(helper, 'left', '0');

    // done
    this._helper = helper;
  }

  add() {
    // append div to parent
    if (this.parent && !this._added) {
      this.parent.appendChild(this._helper);
      this._added = true;
    }
  }

  remove() {
    if (this.parent && this._added) {
      this.parent.removeChild(this._helper);
      this._added = false;
    }
  }

  dispose() {
    this._helper = null;
    this._added = false;
  }

  get el() {
    return this._helper;
  }
}
import {
  Directive,
  ElementRef,
  Renderer2,
  Input,
  Output,
  OnInit,
  HostListener,
  EventEmitter,
  OnChanges,
  SimpleChanges,
  OnDestroy,
  AfterViewInit
} from "@angular/core";

import { Subscription, fromEvent } from "rxjs";
import { IPosition, Position } from "./models/position";
import { HelperBlock } from "./widgets/helper-block";

@Directive({
  selector: "[ngDraggable]",
  exportAs: "ngDraggable"
})
export class AngularDraggableDirective
  implements OnInit, OnDestroy, OnChanges, AfterViewInit {
