  private moving = false;
  private orignal: Position = null;
  private oldTrans = new Position(0, 0, 0, 1, 1);
  private tempTrans = new Position(0, 0, 0, 1, 1);
  private currTrans = new Position(0, 0, 0, 1, 1);
  private oldZIndex = "";
  private _zIndex = "";
  private needTransform = false;

  private draggingSub: Subscription = null;

  /**
   * Bugfix: iFrames, and context unrelated elements block all events, and are unusable
   * https://github.com/xieziyu/ngx-draggable-resize/issues/84
   */
